package id.web.alexanderbryanw;

public class Prak1_6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int bil1, bil2, bil3;
		int hasil;
		bil1 = 5;
		bil2 = 10;
		bil3  = 15;
		System.out.println("HITUNG PERKALIAN");
		System.out.printf("Bilangan pertama adalah %d\n",bil1);
		System.out.printf("Bilangan kedua adalah %d\n",bil2);
		System.out.printf("Bilangan ketiga adalah %d\n",bil3);
		hasil = bil1 * bil2 *bil3;
		System.out.printf("%d * %d * %d = %d", bil1, bil2, bil3, hasil);
	}
}
